define("block_featured_tool/linktracker",["core/ajax"],function(ajax){const Selectors={trackableLink:'[data-action="trackable"]'};const submitLinkData=linkName=>{ajax.call([{methodname:"block_featured_tool_get_clicked_link",args:{link_name:linkName}}])};const init=()=>{document.addEventListener("click",e=>{if(e.target.matches(Selectors.trackableLink)){const linkName=e.target.getAttribute("data-name");submitLinkData(linkName)}},true)};return{init:init}});