define("block_featured_tool/linktracker",["core/ajax"],function(ajax){let eventListenerAdded=false;const Selectors={trackableLink:'[data-action="trackable"]'};const submitLinkData=linkName=>{ajax.call([{methodname:"block_featured_tool_get_clicked_link",args:{link_name:linkName}}])};const getLinkName=()=>{if(eventListenerAdded){return}document.addEventListener("click",e=>{if(e.target.matches(Selectors.trackableLink)){e.preventDefault();const linkName=e.target.getAttribute("data-name");submitLinkData(linkName)}});eventListenerAdded=true};return{init:async()=>{getLinkName()}}});