define("block_featured_tool/linktracker",["core/ajax"],function(ajax){const Selectors={trackableLink:'[data-action="trackable"]'};const submitLinkData=linkName=>{ajax.call([{methodname:"block_featured_tool_getclickedlink",args:{link_name:linkName}}])};const getLinkName=()=>{document.addEventListener("click",e=>{if(e.target.matches(Selectors.trackableLink)){const linkName=e.target.getAttribute("data-name");submitLinkData(linkName)}})};return{init:async()=>{getLinkName()}}});